    ["3"]="<HTML>$B<BODY>$B<H1 align=\"center\">ОФІЦЕРСЬКІ ЗВАННЯ ОРДИ</H1><BR/>$B<P align=\"center\">Частина 2</P>$B<IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/>$B<P align=\"right\">Центуріон</P> P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/>$B<P align=\"right\">Легіонер</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/>$B<P align =\"right\">Blood Guard</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/ >$B<P align=\"right\">Кам\'яний охоронець</P><BR/><BR/>$B</BODY>$B</HTML>",
    ["4"]="<HTML>$B<BODY>$B<H1 align=\"center\">ЗВАННЯ ОРДИ</H1><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank05 \" align=\"left\"/><BR/>$B<P align=\"right\">Перший сержант</P><BR/><BR/>$B<IMG src=\"Interface PvPRankBadgesPvPRank04\" align=\"left\"/><BR/>$B<P align=\"right\">Старший сержант</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/>$B<P align=\"right\">Сержант</P><BR/><BR />$B<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/>$B<P align=\"right\">Grunt</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/>$B<P align=\"right\">Скаут </P><BR/><BR/>$B</BODY>$B</HTML>",
    ["5"]="<HTML>$B<BODY>$B<H1 align=\"center\">ОФІЦЕРСЬКІ ЗВАННЯ АЛЬЯНСУ</H1><BR/>$B<P align=\"center\">Частина 1</P>$B<IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/>$B<P align=\"right\">Великий маршал</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/>$B<P align=\"right \">Фельдмаршал</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/>$B<P align=\"right\">Marshal</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR />$B<P align=\"right\">Commander</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/>$B<P align=\"right\">Лейтенант-командир</P><BR/><BR/>$B</BODY>$B</HTML>",
    ["6"]="<HTML>$B<BODY>$B<H1 align=\"center\">ОФІЦЕРСЬКІ ЗВАННЯ АЛЬЯНСУ</H1><BR/>$B<P align=\"center\">Частина 2</P>$B<IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/>$B<P align=\"right\">Лицар-Чемпіон </P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/>$B<P align=\" справа\">Лицар-капітан</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"ліворуч\"/><BR/>$B<P align=\"right\">Лицар-лейтенант</P><BR/><BR/>$B<IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\" /><BR/>$B<P align=\"right\">Лицар</P><BR/><BR/>$B</BODY>$B</HTML>",
